const cloudinary = require("cloudinary").v2;
cloudinary.config({
  cloud_name: process.env.CLOUDINARY_NAME,
  api_key: process.env.CLOUDINARY_API_KEY,
  api_secret: process.env.CLOUDINARY_API_SECRET
});
exports.handler = async event => {
  const body = JSON.parse(event.body);
  const res = await cloudinary.search
    .expression(body.expression.expression)
    .max_results(3)
    .execute()
    .then(result => result);
  return {
    statusCode: 200,
    body: JSON.stringify(res)
  };
};

import React from "react";
import Button from "./button";
import LazyImage from "../hub/dev-card/image";
import Error from "../svg/error";
import Loading from "../svg/loading";
import { useSearch } from "use-cloudinary";
import { DevCardStateContext } from "../../context/devcard-context";

const SavedProfileImages = () => {
  const { search, data, isLoading, isSuccess, isError, error } = useSearch({ endpoint: "/.netlify/functions/search" });

  // This is just a standard context with some values, such as name
  const state = React.useContext(DevCardStateContext);

  // The folder name cant have any spaces so we replace the spaces
  let folder = `${state.name.replace(/\s/g, "")}-profile`;

  const customConfigSearch = React.useCallback(
    () => search({ expression: `resource_type:image AND folder=${folder}`, config: {} }),
    [search, folder]
  );

  if (isLoading) return <Loading />;
  if (isError)
    return (
      <div>
        <Error width="90px" height="90px" />
        <p>{error.message}</p>
      </div>
    );

  if (state.name.replace(/\s/g, "") !== "" && isSuccess) {
    return (
      <div>
        <div>
          {data &&
            data.resources
              .filter(image => image.folder === getFolder())
              .slice(0, 3)
              .map(image => {
                return (
                  <LazyImage
                    key={image.etag}
                    publicId={image.public_id}
                    width={100}
                    height={100}
                    transforms={{ height: 0.2, border: "2px_solid_black" }}
                    cloudName="xxxxxxx"
                    secure_url={image.secure_url}
                  />
                );
              })}
        </div>
        <em>Your last 3 saves are shown</em>
      </div>
    );
  }

  return (
    <div>
      <Button text="Load Profile Images" onClick={() => customConfigSearch()} />
      <em>Your last 3 saves are shown</em>
    </div>
  );
};

export default SavedProfileImages;

import React from "react";
import { useImage } from "use-cloudinary";
import { DevCardDispatchContext } from "../../../context/devcard-context";

// Image w/ Lazy-load + placeholder
function LazyImage({ publicId, transformations, width, height, cloudName, secure_url, isProfile }) {
  const { generateUrl, blurredPlaceholderUrl, isError, error, ref, supportsLazyLoading, inView, isSuccess } = useImage({
    cloudName
  });
  const dispatch = React.useContext(DevCardDispatchContext);
  React.useEffect(() => {
    generateUrl({
      publicId,
      transformations: {
        width,
        height,
        ...transformations
      }
    });
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  if (isError) return <p>{error.message}</p>;
  return (
    <div
      ref={!supportsLazyLoading ? ref : undefined}
      style={{
        width: `${width}px`,
        height: `${height}px`,
        background: `no-repeat url(${blurredPlaceholderUrl(publicId, width, height)})`,
        cursor: "pointer",
        outline: "none"
      }}
      role="button"
      tabindex="0"
      onKeyPress={() =>
        dispatch({ type: isProfile ? "selectedProfileImage" : "selectedCoverImage", payload: secure_url })
      }
      onClick={() => dispatch({ type: isProfile ? "selectedProfileImage" : "selectedCoverImage", payload: secure_url })}
    >
      {inView || (supportsLazyLoading && isSuccess) ? (
        <img
          src={secure_url}
          loading="lazy"
          style={{
            width: `${width}px`,
            height: `${height}px`,
            border: "solid 2px"
          }}
          alt="Lazy loaded profile"
        />
      ) : null}
    </div>
  );
}

export default LazyImage;

