const React = require("react");
import { FAUNA_SERVER } from "react-native-dotenv";
const {
  ApolloProvider,
  ApolloClient,
  InMemoryCache
} = require("@apollo/client");
const { setContext } = require("apollo-link-context");
const { createHttpLink } = require("apollo-link-http");
const fetch = require("isomorphic-fetch");

const httpLink = createHttpLink({
  uri: "https://graphql.fauna.com/graphql",
  fetch
});

const authLink = setContext((_, { headers }) => {
  return {
    headers: {
      ...headers,
      authorization: `Bearer ${FAUNA_SERVER}`
    }
  };
});

const client = new ApolloClient({
  link: authLink.concat(httpLink),
  cache: new InMemoryCache()
});

const Apollo = ({ children }) => (
  <ApolloProvider client={client}>{children}</ApolloProvider>
);

export default Apollo;

import Apollo from "./constants/apollo";
//.....lots of code

return (
  <Apollo>
    <View style={styles.container}>
      {Platform.OS === "ios" && <StatusBar barStyle="dark-content" />}
      <NavigationContainer linking={LinkingConfiguration}>
        <Stack.Navigator>
          <Stack.Screen name="Root" component={BottomTabNavigator} />
        </Stack.Navigator>
      </NavigationContainer>
    </View>
  </Apollo>
);

type Query {
  allUsers: [User]
}

type User {
  name: String!
  email: String!
}

mutation {
    createUser(
      data: {
        name: "Richard Haines"
        email: "hello@richardhaines.dev"
      }
    ) {
      _id
      name
      email
    }
  }

const dotenv = require("dotenv");
dotenv.config();
module.exports = {
  faunaServerKey: process.env.FAUNA_SERVER
};

const { faunaServer } = require("./config");
const { createHttpLink } = require("apollo-link-http");
const {
  ApolloServer,
  makeRemoteExecutableSchema,
  introspectSchema
} = require("apollo-server-micro");
const fetch = require("isomorphic-fetch");

const link = createHttpLink({
  uri: "https://graphql.fauna.com/graphql",
  fetch,
  headers: {
    Authorization: `Bearer ${faunaServer}`
  }
});

const schema = makeRemoteExecutableSchema({
  schema: introspectSchema(link),
  link
});

const server = new ApolloServer({
  schema,
  playground: true,
  introspection: true
});

module.exports = (req, res, ...args) => {
  if (req.method === "OPTIONS") return res.status(200).send();

  const handler = server.createHandler();

  return handler(req, res, ...args);
};

import { gql, useQuery } from "@apollo/client";

const GET_USERS = gql`
  query GetUsers {
    allUsers {
      data {
        name
        location
        _id
      }
    }
  }
`;

export default function HomeScreen() {

  const {
    loading,
    error,
    data
  } = useQuery(GET_USERS);

  return (
    <View style={styles.container}>
      <ScrollView
        style={styles.container}
        contentContainerStyle={styles.contentContainer}
      >
        <View style={styles.getStartedContainer}>

            {!loading && !error && data.allUsers.data.map((user) => (
              <Text key={user._id}>{user.name}</Text>
            ))}
      </ScrollView>
    </View>
  );
}

